class BloggerProfileBrowseSpider(JuicerSpider):
    name = 'bloggerprofile_browse'
    start_urls = ['http://www.blogger.com/profile-find.g?t=l&loc0=%s' % code for code in COUNTRY_CODES]

    def parse(self, response):
        got_page(self.name, response)

        hdoc = HTML(response)

        for url in hdoc.select_urls(['//@href[contains(., "/profile-find.g")]'], response):
            url = url.split('&ct=')[0]
            get_page(self.name, url)

        for url in hdoc.select_urls('//@href[contains(., "/profile/")]', response):
            url = url.split('&ct=')[0]
            get_page('bloggerprofile_terminal', url)
